package com.robonobo.mina.external.source;

import com.robonobo.mina.external.MinaException;

public class SourceException extends MinaException {

	public SourceException() {
		super();
	}

	public SourceException(String message, Throwable cause) {
		super(message, cause);
	}

	public SourceException(String message) {
		super(message);
	}

	public SourceException(Throwable cause) {
		super(cause);
	}

}
